import React, { useState, useEffect } from "react";
import { Container, Typography, Box, Avatar, Paper } from "@mui/material";
import tictactoeimage from "../assets/Tictactoe.jpeg";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles/TicTacToe.css";

export default function TicTacToe() {
  const initialBoard = [
    [null, null, null],
    [null, null, null],
    [null, null, null],
  ];

  const [board, setBoard] = useState(initialBoard);
  const [startGame, setStartGame] = useState(null);
  const [humanPlay, setHumanPlay] = useState({});
  const [compPlay, setCompPlay] = useState({});
  const [compPlayRow, setCompPlayRow] = useState(null);
  const [compPlayCell, setCompPlayCell] = useState(null);
  const [play, setPlay] = useState("");
  const [lastPlay, setLastPlay] = useState("");
  const [playPos, setPlayPos] = useState(0);

  useEffect(() => {
    handleHumanPlay(play);
    console.log("the value of play is: ", play);
  }, [play]);

  useEffect(() => {
    setTimeout(function () {
      makeComputerPlay();
    }, 500);
    console.log("the value of play position is: ", playPos);
  }, [playPos]);

  useEffect(() => {
    fetch("http://localhost:8080/api/startgame/you", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({}),
    })
      .then((response) => response.json())
      .then((startGame) => setStartGame(startGame));
  }, []);

  if (!startGame) {
    return <div>Loading...</div>;
  }

  const restartGame = () => {
    axios
      .post("http://localhost:8080/api/startgame/you")
      .then((response) => setStartGame(response.json()))
      .catch((error) => console.error(error));
  };

  function findLastPlay(playJson) {
    console.log("PlayJason is: ", playJson.three);
    if (typeof playJson.three === "undefined") setLastPlay(playJson.two);
    else if (typeof playJson.five === "undefined") setLastPlay(playJson.four);
    else setLastPlay("777");
    console.log("made it to the end of findLastPlay. lastPlay is: ", lastPlay);
    return lastPlay;
  }

  function handleComputerPlay(rowIndex, cellIndex) {
    const newBoard = [...board];
    newBoard[rowIndex][cellIndex] = "O";
    setBoard(newBoard);
  }

  function makeComputerPlay() {
    if (playPos % 2 === 0) {
      async function fetchData() {
        try {
          const response = await axios.post(
            "http://localhost:8080/api/computer"
          );
          setCompPlay(response.data);
          // findLastPlay(compPlay);
        } catch (error) {
          console.error(error);
        }
      }

      fetchData();

      console.log("complay: ", compPlay, "play position: ", playPos);

      if (playPos === 2) {
        setLastPlay(compPlay.two);
      } else if (playPos === 4) {
        setLastPlay(compPlay.four);
      } else if (playPos === 6) {
        setLastPlay(compPlay.six);
      }

      // map computer play to APIPlay
      // findLastPlay(compPlay);
      if (lastPlay.slice(-2) === "00") {
        setCompPlayRow(0);
        setCompPlayCell(0);
      } else if (lastPlay.slice(-2) === "01") {
        setCompPlayRow(0);
        setCompPlayCell(1);
      } else if (lastPlay.slice(-2) == "02") {
        setCompPlayRow(0);
        setCompPlayCell(2);
      } else if (lastPlay.slice(-2) === "10") {
        setCompPlayRow(1);
        setCompPlayCell(0);
      } else if (lastPlay.slice(-2) === "11") {
        setCompPlayRow(1);
        setCompPlayCell(1);
      } else if (lastPlay.slice(-2) === "12") {
        setCompPlayRow(1);
        setCompPlayCell(2);
      } else if (lastPlay.slice(-2) === "20") {
        setCompPlayRow(2);
        setCompPlayCell(0);
      } else if (lastPlay.slice(-2) === "21") {
        setCompPlayRow(2);
        setCompPlayCell(1);
      } else if (lastPlay.slice(-2) === "22") {
        setCompPlayRow(2);
        setCompPlayCell(2);
      }
      // make computer play
      console.log("lastPlay slice: ", lastPlay.slice(-2));
      console.log(compPlayRow, compPlayCell);
      // handleComputerPlay(compPlayRow, compPlayCell);
    }
  }

  // const handleCellClickComp = (rowIndex, cellIndex) => {
  //   const newBoard = [...board];
  //   newBoard[rowIndex][cellIndex] = "O";
  //   setBoard(newBoard);
  // };

  const handleCellClick = (rowIndex, cellIndex) => {
    const newBoard = [...board];
    newBoard[rowIndex][cellIndex] = "X";
    setBoard(newBoard);

    //Map play to API Play
    if (rowIndex === 0 && cellIndex === 0) setPlay("0,0");
    else if (rowIndex === 0 && cellIndex === 1) setPlay("0,1");
    else if (rowIndex === 0 && cellIndex === 2) setPlay("0,2");
    else if (rowIndex === 1 && cellIndex === 0) setPlay("1,0");
    else if (rowIndex === 1 && cellIndex === 1) setPlay("1,1");
    else if (rowIndex === 1 && cellIndex === 2) setPlay("1,2");
    else if (rowIndex === 2 && cellIndex === 0) setPlay("2,0");
    else if (rowIndex === 2 && cellIndex === 1) setPlay("2,1");
    else if (rowIndex === 2 && cellIndex === 2) setPlay("2,2");
  };

  // function handleHumanPlay(play) {
  // let url = `http://localhost:8080/api/humanplayer/${play}`;
  // if (url != "http://localhost:8080/api/humanplayer/") {
  //   axios
  //     .post(url)
  //     .then((response) => setStartGame(response.data))
  //     .catch((error) => console.error(error));
  // }
  // setPlayPos(playPos + 2);
  // setTimeout(function () {
  //   makeComputerPlay();
  // }, 500);
  // }

  async function handleHumanPlay(play) {
    let url = `http://localhost:8080/api/humanplayer/${play}`;
    if (url != "http://localhost:8080/api/humanplayer/") {
      try {
        const response = await axios.post(url);
        setHumanPlay(response.data);
        setPlayPos(playPos + 2);
      } catch (error) {
        console.error(error);
      }
    }
  }

  return (
    <Container maxWidth="md" sx={{ marginTop: "2rem" }}>
      <Typography variant="h3" gutterBottom>
        {/* Sola Akinbode */}
      </Typography>
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "2rem",
          backgroundColor: "#f0f0f0",
        }}
      >
        <Avatar
          src={tictactoeimage}
          sx={{ width: 150, height: 150, marginRight: "1rem" }}
          alt="Picture"
        />
        <Typography variant="h4" style={{ color: "blue", fontStyle: "italic" }}>
          Tic-Tac-Toe
        </Typography>
      </Box>
      <Paper sx={{ padding: "2rem" }}>
        <div className="board">
          {board.map((row, rowIndex) => (
            <div className="row" key={rowIndex}>
              {row.map((cell, cellIndex) => (
                <button
                  className={`cell ${cell}`}
                  key={cellIndex}
                  onClick={() => handleCellClick(rowIndex, cellIndex)}
                >
                  {board[rowIndex][cellIndex]}
                </button>
              ))}
            </div>
          ))}
        </div>
      </Paper>
    </Container>
  );
}
